package com.example.kuaikuait.screen;import android.app.Activity;import android.content.Intent;import android.database.Cursor;import android.database.sqlite.SQLiteDatabase;import android.graphics.PixelFormat;import android.os.Bundle;import android.view.Gravity;import android.view.View;import android.view.WindowManager;import android.widget.Button;import android.widget.ListView;import android.widget.TextView;public class MainActivity extends Activity implements View.OnClickListener{    private Button btnToAdd;    private ListView lv;    private Intent i;    private MyAdapter adapter;    private NotesDB notesDB;    private SQLiteDatabase dbReader;    private Cursor cursor;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        btnToAdd= (Button) findViewById(R.id.btn_toadd);        btnToAdd.setOnClickListener(this);        lv= (ListView) findViewById(R.id.list);        notesDB = new NotesDB(this);        dbReader = notesDB.getReadableDatabase();        FloatView.showfloatview(MainActivity.this);    }    @Override    public void onClick(View v) {        i=new Intent(this,AddContent.class);        switch (v.getId()){            case R.id.btn_toadd:                i.putExtra("flag","1");                startActivity(i);                break;        }    }    public void selectDB() {        cursor = dbReader.query(NotesDB.TABLE_NAME, null, null, null, null,                null, null);        adapter = new MyAdapter(this, cursor);        lv.setAdapter(adapter);    }    protected void onResume() {        super.onResume();        selectDB();    }}